mapper.select:
SELECT bbs_no as bbsNo, bbs_title as bbsTitle, bbs_content as bbsContent,bbs_writer as bbsWriter, bbs_reg_date as bbsRegDate, bbs_count as bbsCount
FROM bbs 
WHERE bbs_no = #{bbsNo}

mapper.update:
UPDATE bbs
SET  bbs_title=#{bbsTitle}, bbs_content=#{bbsContent} 
WHERE bbs_no=#{bbsNo}
